import React, {useState, useEffect} from 'react'
import {Button, IconButton} from '@material-ui/core'
import {DataGrid} from '@material-ui/data-grid'
import BootstrapTable from 'react-bootstrap-table-next';
import {Container} from 'react-bootstrap';
import ViewListIcon from '@material-ui/icons/ViewList';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import AddIcon from '@material-ui/icons/Add';
import ViewModuleIcon from '@material-ui/icons/ViewModule';
import ToggleButton from '@material-ui/lab/ToggleButton';
import ToggleButtonGroup from '@material-ui/lab/ToggleButtonGroup';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import EditModal from './EditModal'


function ListSearches({searchTerm}) {
    const [tableData, setTableData] = useState([])
    const [selectionModel, setSelectionModel] = useState([])
    const [view, setView] = useState('list');
    const [edit, setEdit] = useState(false);

    const handleChange = (event, nextView) => {
      setView(nextView);
    };
    const editBtn = () => {
        if(edit === true){
            setEdit(false)
        } else {
            setEdit(true)
        }
        console.log(selectionModel)
    }
    const columns = [
        {dataField: 'firstname', text: "First Name"},
        {dataField: 'lastname', text: "Last Name"},
        {dataField: 'company', text: "Company"},
        {dataField: 'department', text: "Department"},
        {dataField: 'jobtitle', text: "Job Title"},
        {dataField: 'email', text: "Email"},
        {dataField: 'phone', text: "Phone"},

    ]

    const selectRow = {
        mode: 'checkbox',
        clickToSelect: true
      };

    useEffect(()=>{
        setTableData(searchTerm)
    })

    return (
        <Container>

            {searchTerm ? 
            <div>
                <div style={{display: "flex"}}>
                    <ToggleButtonGroup orientation="horizontal" value={view} exclusive onChange={handleChange}>
                        <ToggleButton value="list" aria-label="list">
                            <ViewListIcon />
                        </ToggleButton>
                        <ToggleButton value="module" aria-label="module">
                            <ViewModuleIcon />
                        </ToggleButton>
                    </ToggleButtonGroup>
                    {
                        selectionModel.length < 1 ?
                        <ButtonGroup style={{alignItems: "flex-end"}}>
                        <IconButton aria-label="Add">
                            <AddIcon />
                        </IconButton>
                        </ButtonGroup> : <></>
                    }
                    {
                        selectionModel.length === 1 ? 
                        <ButtonGroup>
                            <IconButton aria-label="Edit">
                                <EditIcon onClick={editBtn} />
                            </IconButton>
                            <IconButton aria-label="Delete">
                                <DeleteIcon />
                            </IconButton>
                        </ButtonGroup>   
                       
                        : <></>
                    }
                    {
                        selectionModel.length > 1 ? 
                        <ButtonGroup>
                            <IconButton aria-label="Delete">
                                <DeleteIcon />
                            </IconButton>
                        </ButtonGroup>                           
                        : <></>
                    }
                    {
                        edit ? <EditModal view={edit} profile={selectionModel} /> : <></>
                    }
                </div>
                <div style={{height: 650, width: '100%', marginTop: "15px"}}>
                    <BootstrapTable
                    keyField='id'
                    rows={tableData}
                    columns={columns}
                    selectRow={ selectRow }
                    /> </div> </div> : <p></p>    
            }
        
            </Container>
    )
}

export default ListSearches
