import React from 'react';
import { Form, Button, Card} from "react-bootstrap"
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import MaskedInput from 'react-text-mask';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

export default function TransitionsModal({view, profile}) {
  const classes = useStyles();
  const [open, setOpen] = React.useState(view);
  const [edit, setEdit] = React.useState(true)

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const editForm = () => {
    if (edit === false) {
        setEdit(true)
    } else {
        setEdit(false)
    }
  }

  return (
    <div>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
        <Card style={{width: "35%"}}>
        <Card.Body>
          <h2 className="text-center mb-4">Edit Contact</h2>
          <Form >
            <Form.Group id="firstname">
              <Form.Label>First Name</Form.Label>
              <Form.Control disabled={edit} placeholder={profile.firstname}/>
            </Form.Group>              
            <Form.Group id="lastname">
              <Form.Label>Last Name</Form.Label>
              <Form.Control disabled={edit}/>
            </Form.Group>
            <Form.Group id="email">
              <Form.Label>Email</Form.Label>
              <Form.Control disabled={edit}/>
            </Form.Group>
            <Form.Group id="phone">
              <Form.Label>Phone</Form.Label><br></br>
              <MaskedInput mask={['(', /[1-9]/, /\d/, /\d/, ')', ' ', /\d/, /\d/, /\d/, '-', /\d/, /\d/, /\d/, /\d/]} 
              placeholderChar={'\u2000'} 
              showMask 
              style={{
                width: "100%",
                height: "10%"
              }}
              disabled={edit}/>
            </Form.Group>
            <Form.Group id="streetaddress">
              <Form.Label>Street Address</Form.Label>
              <Form.Control disabled={edit}/>
            </Form.Group>
            <Button className="w-100 mt-3" onClick={editForm}>
              Edit
            </Button>
            <Button className="w-100 mt-3" onClick={handleClose}>
              Close
            </Button>
          </Form>
        </Card.Body>
      </Card>
        </Fade>
      </Modal>
    </div>
  );
}
