import React, {useState, useEffect} from 'react';
import axios from 'axios';
import NavBar from '../page components/NavBar';
import AddFolder from '../page components/AddFolder';
import Folder from '../page components/Folder';

function Bookmarks() {
    const [user, setUser] = useState([])
    const [currentFolder, setCurrentFolder] = useState([])
    const [allFolders, setAllFolders] = useState([])

    const getCurrentUser = async () => {
        let currentUser = await axios({
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${localStorage.getItem("authToken")}`
            },
            url: "http://localhost:5000/api/private/get/currentuser",
            data: {
                id: "1001"
            }
        })
        setUser(currentUser.data)
        console.log(user)
    }

    useEffect(() => {
        getCurrentUser()
    }, [])

    const getCurrentFolder = async () => {
            let getcurrentFolder = await axios({
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${localStorage.getItem("authToken")}`
                },
                url: "http://localhost:5000/api/private/get/currentfolder",
                data: {
                    id: "ROOT"
                }
            })
            setCurrentFolder(getcurrentFolder.data)
            console.log(getcurrentFolder.data)
    }

    useEffect(() => {
        getCurrentFolder()
    }, [])

    const getAllFolders = async () => {
        let getAllFolder = await axios({
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${localStorage.getItem("authToken")}`
            },
            url: "http://localhost:5000/api/private/get/folders",
        })
        setAllFolders(getAllFolder.data)
        console.log(getAllFolder.data)
}

useEffect(() => {
    getAllFolders()
}, [])

    return (
        <div>
            <NavBar />
            <section style={{marginLeft: "90%"}}>
            <AddFolder currentUser={user} currentFolder={currentFolder} />
            </section>
            <section style={{marginLeft: "1%"}} >
            {
                currentFolder === null ? <Folder currentFolder={currentFolder} /> : <p>1</p>
            }
            {
                allFolders.length > 0 ? <Folder currentFolder={allFolders} /> : <p></p>
            }
            </section>
        </div>
    )
}

export default Bookmarks
