import React from 'react';
import { Form, Button, Card} from "react-bootstrap"
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '2px solid #000',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

function EditModal({view, profile, close}) {
  const classes = useStyles();
  const [open, setOpen] = React.useState(view);
  const [edit, setEdit] = React.useState(true)

  // const handleOpen = () => {
  //   setOpen(true);
  // };

  // const handleClose = () => {
  //   setOpen(false);
  // };

  const editForm = () => {
    if (edit === false) {
        setEdit(true)
    } else {
        setEdit(false)
    }
  }

  return (
    <div>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={close}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
        <Card style={{width: "35%"}}>
        <Card.Body>
          <h2 className="text-center mb-4">Edit Contact</h2>
          <Form >
            <Form.Group id="firstname">
              <Form.Label>First Name</Form.Label>
              <Form.Control disabled={edit} value={profile.firstname}/>
            </Form.Group>              
            <Form.Group id="lastname">
              <Form.Label>Last Name</Form.Label>
              <Form.Control disabled={edit} value={profile.lastname}/>
            </Form.Group>
            <Form.Group id="email">
              <Form.Label>Email</Form.Label>
              <Form.Control disabled={edit} value={profile.email}/>
            </Form.Group>
            <Form.Group id="phone">
              <Form.Label>Phone</Form.Label><br></br>
              <Form.Control disabled={edit} value={profile.phone}/>
            </Form.Group>
            <Form.Group id="company">
              <Form.Label>Employer</Form.Label>
              <Form.Control disabled={edit} value={profile.company}/>
            </Form.Group>
            <Form.Group id="department">
              <Form.Label>Department</Form.Label>
              <Form.Control disabled={edit} value={profile.department}/>
            </Form.Group>
            <Form.Group id="jobtitle">
              <Form.Label>Job Title</Form.Label>
              <Form.Control disabled={edit} value={profile.jobtitle}/>
            </Form.Group>
            <Button className="w-100 mt-3" onClick={editForm}>
              Edit
            </Button>
            <Button className="w-100 mt-3" onClick={close}>
              Close
            </Button>
          </Form>
        </Card.Body>
      </Card>
        </Fade>
      </Modal>
    </div>
  );
}

export default EditModal
