import React, {useState} from 'react'
import { Form, Button, Card} from "react-bootstrap"
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';

const useStyles = makeStyles((theme) => ({
    modal: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    paper: {
      backgroundColor: theme.palette.background.paper,
      border: '2px solid #000',
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2, 4, 3),
    },
  }));

function AddContact({open, close}) {
    const classes = useStyles();

    const [firstname, setFirstname] = useState("")
    const [lastname, setLastname] = useState("")
    const [email, setEmail] = useState("")
    const [phone, setPhone] = useState("")
    const [company, setCompany] = useState("")
    const [department, setDepartment] = useState("")
    const [jobTitle, setJobTitle] = useState("")

    const addContact = async () => {
        
    }

    return (
    <div>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={close}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
        <Card style={{width: "35%"}}>
        <Card.Body>
          <h2 className="text-center mb-4">Edit Contact</h2>
          <Form >
            <Form.Group id="firstname">
              <Form.Label>First Name</Form.Label>
              <Form.Control onChange={(e)=> setFirstname(e.target.value)}/>
            </Form.Group>              
            <Form.Group id="lastname">
              <Form.Label>Last Name</Form.Label>
              <Form.Control onChange={(e)=> setLastname(e.target.value)}/>
            </Form.Group>
            <Form.Group id="email">
              <Form.Label>Email</Form.Label>
              <Form.Control onChange={(e)=> setEmail(e.target.value)}/>
            </Form.Group>
            <Form.Group id="phone">
              <Form.Label>Phone</Form.Label><br></br>
              <Form.Control onChange={(e)=> setPhone(e.target.value)}/>
            </Form.Group>
            <Form.Group id="company">
              <Form.Label>Employer</Form.Label>
              <Form.Control onChange={(e)=> setCompany(e.target.value)}></Form.Control>
            </Form.Group>
            <Form.Group id="department">
              <Form.Label>Department</Form.Label>
              <Form.Control onChange={(e)=> setDepartment(e.target.value)}/>
            </Form.Group>
            <Form.Group id="jobtitle">
              <Form.Label>Job Title</Form.Label>
              <Form.Control onChange={(e)=> setJobTitle(e.target.value)}/>
            </Form.Group>
            <Button className="w-100 mt-3" onClick={()=> console.log("submit")}>
              Add Contact
            </Button>
            <Button className="w-100 mt-3" onClick={close}>
              Close
            </Button>
          </Form>
        </Card.Body>
      </Card>
        </Fade>
      </Modal>
    </div>
    )
}

export default AddContact
