import React, { useState, useEffect } from "react";
import axios from 'axios';
import { Button, Modal, Form } from "react-bootstrap";
import {IconButton, Select, MenuItem, InputLabel, FormControl} from '@material-ui/core';
import StarIcon from '@material-ui/icons/Star';


export default function AddFolderButton({ currentContact }) {
    const [allFolders, setAllFolders] = useState([])
    const [selectFolder, setSelectFolder] = useState([])
    const [open, setOpen] = useState(false)
     const [name, setName] = useState("")

  const getAllFolders = async () => {
      let Folders = await axios({
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("authToken")}`
        },
        url: "http://localhost:5000/api/private/get/folders",
      })
    console.log(Folders)
    setAllFolders(Folders.data)
  }

  function openModal() {
    setOpen(true)
    getAllFolders()
  }

  function closeModal() {
    setOpen(false)
  }

  function handleSubmit(e) {
    e.preventDefault()
  }

  function handleChange(e){
      setSelectFolder(e.target.value)
  }

  return (
    <>
      <IconButton onClick={openModal} variant="outline-success" size="sm">
        <StarIcon />
      </IconButton>
      <Modal show={open} onHide={closeModal}>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            <FormControl>
              <InputLabel id="folder-select-label">Choose Folder</InputLabel>
              <Select value={selectFolder} onChange={handleChange} labelId="folder-select-label">
                  <MenuItem value="ROOT">Main Page</MenuItem>
              {
                  allFolders.map(keyWord => <MenuItem value={keyWord._id}>{keyWord.folderName}</MenuItem>)
              }
              </Select>
            </FormControl>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={closeModal}>
              Close
            </Button>
            <Button variant="success" type="submit">
              Add Folder
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </>
  )
}
