import React, {useState, useEffect} from 'react';
import axios from 'axios';
import NavBar from '../page components/NavBar';
import AddFolder from '../page components/AddFolder';
import Folder from '../page components/Folder';

function Bookmarks() {
    const [user, setUser] = useState([])
    const [currentFolder, setCurrentFolder] = useState([])

    const getCurrentUser = async () => {
        let currentUser = await axios({
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${localStorage.getItem("authToken")}`
            },
            url: "http://localhost:5000/api/private/get/currentuser",
            data: {
                id: "1001"
            }
        })
        setUser(currentUser.data)
        console.log(user)
    }

    useEffect(() => {
        getCurrentUser()
    }, [])

    const getCurrentFolder = async () => {
        let currentFolder = await axios({
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                Authorization: `Bearer ${localStorage.getItem("authToken")}`
            },
            url: "http://localhost:5000/api/private/get/currentfolder",
            data: {
                id: user._id
            }
        })
        setCurrentFolder(currentFolder.data[0])
    }

    useEffect(() => {
        getCurrentFolder()
    }, [])

    return (
        <div>
            <NavBar />
            <section>
            <AddFolder currentUser={user} currentFolder={currentFolder} />
            </section>
            <section style={{marginLeft: "1%"}} >
            {
                currentFolder.parentID === "ROOT" ? <Folder currentFolder={currentFolder} /> : <p></p>
            }
            </section>
        </div>
    )
}

export default Bookmarks
