import React, { useState, useEffect } from "react";
import axios from 'axios';
import { Button, Modal, Form } from "react-bootstrap";
import {IconButton, Select, MenuItem, InputLabel} from '@material-ui/core';
import StarIcon from '@material-ui/icons/Star';


export default function AddFolderButton({ currentContact, getAllFolders }) {
    // const [allFolders, setAllFolders] = useState([])
    const [open, setOpen] = useState(false)
     const [name, setName] = useState("")

//   const getAllFolders = async () => {
//       let Folders = await axios({
//           method: "GET",
//           headers: {
//             "Content-Type": "application/json",
//             Authorization: `Bearer ${localStorage.getItem("authToken")}`
//         },
//         url: "http://localhost:5000/api/private/get/folders",
//       })
//     console.log(Folders)
//     setAllFolders(Folders.data)
//   }

//   useEffect(()=> {
//       getAllFolders()
//   })

  function openModal() {
    setOpen(true)
  }

  function closeModal() {
    setOpen(false)
  }

  function handleSubmit(e) {
    e.preventDefault()
  }

  return (
    <>
      <IconButton onClick={openModal} variant="outline-success" size="sm">
        <StarIcon />
      </IconButton>
      <Modal show={open} onHide={closeModal}>
        <Form onSubmit={handleSubmit}>
          <Modal.Body>
            <Form.Group>
              <InputLabel>Choose Folder</InputLabel>
              {
                  getAllFolders.map(keyWord => <MenuItem>{keyWord.foldername}</MenuItem>)
              }
              
            </Form.Group>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={closeModal}>
              Close
            </Button>
            <Button variant="success" type="submit">
              Add Folder
            </Button>
          </Modal.Footer>
        </Form>
      </Modal>
    </>
  )
}
